/* Optional local fallback for html2pdf.js. If you have internet, CDN will be used. */
/* Minimal stub to prevent runtime errors if offline and not yet cached. */
(function(){
  if (window.html2pdf) return; // If already present, do nothing
  function notReady(){
    alert('PDF engine not loaded yet. Open the site once online or try Print CV.');
  }
  window.html2pdf = function(){
    return { set: function(){ return this; }, from: function(){ return this; }, save: function(){ notReady(); return Promise.resolve(); } };
  };
})();
